    public boolean chkAndLoadToObject()
    {
        if(table_keyword.isEditing())
        { table_keyword.getCellEditor().stopCellEditing(); }
        
        boolean flag=true;
        String value="";
        
        if(count==0)
        {
            //System.out.println("No Entry in List!");
            //LanguageEditor.setMessages("No entry in the List! No data was saved!", "", 0);
            CATModeler.data.addKeywords(null, null);
            return true; 
        }
        else
        {
            for(int r=0;r<count;r++)
            {                
                    value=String.valueOf(table_keyword.getModel().getValueAt(r,0));
                    if(value.equals(""))
                    {
                        flag=false;
                        break;
                    }                              
            }
            
            if(textF_ret.getText().equals(""))
            { flag=false; }
        }
        
        if(flag)
        {
            //ModelData data = new ModelData();    //delete
                        
            String tmp[]= new String[count];
            for(int r=0;r<count;r++)
            {             
                tmp[r]=String.valueOf(table_keyword.getModel().getValueAt(r,0));                
            }
              
            CATModeler.data.addKeywords(tmp, textF_ret.getText());
            
            //LanguageEditor.data.showData();            
            //LanguageEditor.setMessages("Input Set Saved Successfully!", "Saved!", 0);                        
        }
        else
        {
            CATModeler.setMessages("Keyword | Denied! Please fill all entries!", "", 0);
        }
        
        return flag;
    }
    
    public void loadData()
    {
        count=CATModeler.data.loadKeywrdFrm(table_keyword,textF_ret);
        textF_cnt.setText(String.valueOf(count));
    }
    
